package Hello_World;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Unary Operator --   ++  making increment of variable
          int a = 5;
             a = a++; // Increment that variable and assign it to 
             System.out.println(a);
             a = ++a; // Increment that variable and assign it to 
             System.out.println(a);
	}

}
